import React, { useState, useEffect } from 'react';
import { DataGrid } from '@material-ui/data-grid';
import { Button, ButtonGroup, Container, TextField } from '@material-ui/core';
import api from '../api'
import { makeStyles } from '@material-ui/core/styles';

let rows = [
]
const columns = [
    { field: 'type', headerName: 'type', width: 150 },
    { field: 'name', headerName: 'name', width: 150 },
    { field: 'picture', headerName: 'picture', width: 150 },
    { field: 'price', headerName: 'price', width: 150 },
];

const useStyles = makeStyles({
    root: {
    //   background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    //   borderRadius: 3,
    //   border: 0,
      color: 'white',
    //   height: 48,
    //   padding: '0 30px',
    //   boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    },
    // label: {
    //   textTransform: 'capitalize',
    // },
  });

export function ProductList(props) {
    const [selection, setSelection] = useState([]);
    const [products, setProducts] = useState([0]);
    const [edit, setEdit] = useState([]);
    const classes = useStyles();

    const changeProduct = async () => {
        await api.getProductById(selection).then(prod => {
            setEdit(prod.data);
        })

    }

    const EditProduct = async () => {
        await api.updateProductById(selection);
        load();
    }
    // edit();
    // }
    // )

    const DeleteProduct = async () => {
        if (window.confirm(`Do you want to delete the product ${selection} permanently`)) {
            await api.deleteProductById(selection);
            load();
        }
    }

    const AddProduct = () => {
        window.location.href = `/product/new`;
    }

    async function load() {
        rows = [];
        await api.getAllProducts().then(products => {
            products.data.map(product => (rows.push({ id: product._id, type: product.type, name: product.name, picture: product.picture, price: product.price })));
            setProducts(products.data);
        });
    }

    useEffect(() => {
        async function load() {
            rows = [];
            await api.getAllProducts().then(products => {
                products.data.map(product => (rows.push({ id: product._id, type: product.type, name: product.name, picture: product.picture, price: product.price })));
                setProducts(products.data);
            });
        }
        load();
    }, []);
    return (
        <div style={{ height: 700, width: '100%', display: 'flex', color: "white" }}>
            <DataGrid id={rows._id} rows={rows} columns={columns} pageSize={10} checkboxSelection
                onSelectionModelChange={(newSelection) => {
                    setSelection(newSelection.selectionModel);
                }} selectionModel={selection} classes={{root: classes.root}} />
            <div style={{ width: '30%', color: 'white' }}>
                <ButtonGroup>
                    {/* <Button variant="contained" onClick={load}>Load Data</Button> */}
                    <Button variant="contained" color="primary" onClick={changeProduct}>Edit Data</Button>
                    <Button variant="contained" color="secondary" onClick={DeleteProduct}>Delete Data</Button>
                    <Button variant="contained" color="primary" onClick={AddProduct}>Add Data</Button>
                </ButtonGroup>
                <Container style={{ width: '200px', color: 'white', display: 'flex', flexDirection: 'column' }}>
                    <form onSubmit={EditProduct} method="POST">
                        <TextField label="type" type="text" name="type" id="type" value={edit.type || ''} />
                        <TextField label="name" type="text" name="name" id="name" value={edit.name || ''} />
                        <TextField label="picture" type="text" name="picture" id="picture" value={edit.picture || ''}/>
                        {/* <input accept="image/*" id="contained-button-file" multiple type="file" hidden />
                        <label htmlFor="contained-button-file">
                            <Button variant="contained" color="primary" component="span">Upload</Button>
                        </label> */}
                        <TextField label="price" type="text" name="price" id="price" value={edit.price || 0} />
                        <Button color="primary" type="submit">Save</Button>{' '}
                    </form>
                </Container>
            </div>
        </div >
    );
}
