import React from 'react';
import { Link } from 'react-router-dom';
import { Button, ButtonGroup, Container, Table } from 'reactstrap'
import api from '../api'

export class ProductList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { products: [] };
        this.remove = this.remove.bind(this);
    }

    componentDidMount = async () => {
        await api.getAllProducts().then(products => {
            this.setState({
                products: products.data.data,
                // isLoading: false,
            })
        })
        // fetch('/api/products')
        //     .then(response => response.json())
        //     .then(data => this.setState({ products: data }));
    }

    deleteUser = event => {
        event.preventDefault();
        if (window.confirm(`Do you want to delete the product ${this.props.id} permanently`)) {
            api.deleteProductById(this.props.id);
            window.location.reload();
        }
    }
    // await fetch(`/api/product/${id}`, {
    //     method: 'DELETE',
    //     headers: {
    //         "Accept": "application/json",
    //         "Content-type": "application/json"
    //     }
    // }).then(() => {
    //     let updatedProducts = [...this.state.products].filter(i => i.id !== id);
    //     this.setState({ products: updatedProducts });
    // });


    render() {
        const { products } = this.state;
        const productList = products.map(product => {
            return <tr key={product.id}>
                <td style={{ whiteSpace: 'nowrap' }}>{product.type}</td>
                <td>{product.name}</td>
                <td>{product.picture}</td>
                <td>{product.price}</td>
                <td>
                    <ButtonGroup>
                        <Button to={'/products/' + product.id}>Edit</Button>
                        <Button onClick={() => this.remove(product.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });
        return (
            <div>
                <Container fluid>
                    <div>
                        <Button tag={Link} to="/products/new">Add Product</Button>
                    </div>
                    <h3>Product List</h3>
                    <Table>
                        <thead>
                            <tr>
                                <th width="25%">type</th>
                                <th width="25%">name</th>
                                <th width="25%">picture</th>
                                <th width="25%">price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {productList}
                        </tbody>
                    </Table>
                </Container>
            </div>
        );
    }
}

