import React from 'react';
import '../App.css';
import api from '../api';
import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';

export class Cups extends React.Component {
    constructor(props) {
        super(props);
        this.state = { products: [] };
    }

    async componentDidMount() {
        await api.getAllProducts().then(products => {
            this.setState({
                products: products.data,
            })
        })
    }
    render() {
        return (
            // <Grid container spacing={3}>
            <div>
                {this.state.products.map((product, i) => (
                    <Grid item justify="center" key={i} xs={3} className='contentBlock'>
                        <Paper className="paper">
                            <li><img src={'/imgs/' + product.picture + '.jpg'} alt={product.name + ' picture'} className="picture" /></li>
                            <li className="productName">{product.name}</li>
                            {/* <li>{product.type}</li> */}
                            <li className="productPrice">{'₴' + product.price}</li>
                            <li className="productBuy"><button tag={Link} to="/order">Купить</button></li>
                        </Paper>
                    </Grid>
                ))}
            </div>
            // </Grid>
        )
    }
}

export default Cups;
