import React, { useState, useEffect } from 'react';
import '../App.css';
import api from '../api';
// import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';
// import FormDialog from '../components/dialog.form';

export function Cups() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function load() {
            await api.getAllProducts().then(products => {
                setProducts(products.data.filter(product => (product.type === 'cup')))
            })
        }
        load();
    }, [])

    const handleBuy = (product, i) =>{
        localStorage.setItem(`productId${i}`, product._id);
    }

    return (
        <Grid container className="contentWrapper" spacing={1} style={{ 'width': '70%', margin: 'auto' }}>
            {products.map((product, i) => (
                <Grid item justify="center" key={i} alignItems="center" xs={2.5} className='contentBlock' style={{ margin: '10px 10px' }}>
                    <Paper className="paper">
                        <li><img src={'/imgs/' + product.picture + '.jpg'} alt={product.name + ' picture'} className="picture" /></li>
                        <li className="productName">{product.name}</li>
                        <li className="productPrice">{'₴' + product.price}</li>
                        <li className="productBuy"><Button variant="contained" onClick={handleBuy}/*tag={Link} to="/order"*/>Купить</Button></li>
                    </Paper>
                </Grid>
            ))}
        </Grid>
    )
}

export default Cups;
