import React, { useState } from 'react';
// import { Link } from 'react-router-dom';
// import React from 'react';
// import { makeStyles } from '@material-ui/core/styles';
import { DataGrid } from '@material-ui/data-grid';
// import ReactTable from 'react-table'
// import { Button, ButtonGroup, Container, Table } from 'reactstrap'
import api from '../api'

// import styled from 'styled-components'

import styled from 'styled-components'
// import { useTable } from 'react-table'

import { withRouter } from 'react-router-dom';

// import 'react-table/react-table.css'

// const Wrapper = styled.div`
//     padding: 0 40px 40px 40px;
// `

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

class UpdateProduct extends React.Component {
    updateUser = event => {
        event.preventDefault()

        window.location.href = `/products/update/${this.props.id}`
    }

    render() {
        return <Update onClick={this.updateUser}>Update</Update>
    }
}

class DeleteProduct extends React.Component {
    constructor(props) {
        super(props);
        // this.selection = [];
        // this.selection = props.selection;
        console.log('DELETE ' + this.selection);
        console.log(this.props)
    }
    deleteUser = event => {
        event.preventDefault();
        console.log(this.props.selection.selectionModel);
        if (window.confirm(`Do you want to delete the product ${this.props.id} permanently`)) {
            this.props.selection.selectionModel.map(element => (api.deleteProductById(element)));
            // api.deleteProductById(this.selection.selectionModel);
            window.location.reload();
        }
    }
    render() {
        return <Delete onClick={this.deleteUser}>Delete</Delete>
    }
}

export class ProductList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { products: [], selection: [] };
        this.load = this.load.bind(this);
        this.columns = [];
        this.rows = [];
        this.selection = [];
        this.returnSelection = this.returnSelection.bind(this);
    }

    componentDidMount = async () => {
        await api.getAllProducts().then(products => {
            this.setState({
                products: products.data,

            })
            this.load();
        })
    }

    componentDidUpdate = async (prevProps, prevState) => {
        await api.getAllProducts().then(products => {
            this.setState({
                products: products.data,
            })
            // this.load();
        })
        console.log(prevState);
        if (this.state.products !== prevState.products) {
            this.load();
            // await api.getAllProducts().then(products => {
            //     this.setState({
            //         products: products.data,
            //     })
            //     // this.load();
            // })
        }
    }

    async load() {
        const { products } = this.state;
        try {
            console.log('TCL: ProductList -> render -> products', products);
            for (let i = 0; i < products.length; i++) {
                this.rows.push({ id: products[i]._id, type: products[i].type, name: products[i].name, picture: products[i].picture, price: products[i].price });
            }
        }
        catch (error) {
            console.log(error);
        }
        const rows = [
        ]
        const columns = [
            { field: 'type', headerName: 'type', width: 150 },
            { field: 'name', headerName: 'name', width: 150 },
            { field: 'picture', headerName: 'picture', width: 150 },
            { field: 'price', headerName: 'price', width: 150 },
        ];
        this.columns = columns;
        this.rows = rows;
    }

    async returnSelection(newSelection) {
        this.setState({ selection: newSelection });
    }

    render() {
        this.load();

        return (
            <div style={{ height: 700, width: '100%' }}>
                <DataGrid id={this.rows._id} rows={this.rows} columns={this.columns} pageSize={10} checkboxSelection
                    onSelectionModelChange={(newSelection) => {
                        // this.selection = newSelection;
                        this.returnSelection(newSelection);
                        console.log(this.selection.selectionModel);
                    }} />
                <UpdateProduct selection={this.selection} />
                <DeleteProduct selection={this.selection} />
            </div >
        );
    }
}

export default withRouter(ProductList);
