export class ProductList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { products: [] };
        // this.remove = this.remove.bind(this);
    }

    componentDidMount = async () => {
        await api.getAllProducts().then(products => {
            console.log(products.data);
            this.setState({
                products: products.data,
                // isLoading: false,
            })
        })
    }


    render() {
        const { products } = this.state;
        console.log('1' + products);
        const rows = [];
        setTimeout(() => {
            try {
                if (products.length !== Number) {
                    console.log('No data loaded...');
                    return 'No data loaded...';
                }
                else {

                    console.log('TCL: ProductList -> render -> products', products);
                    function createData(product) {
                        return [product._id, product.name, product.type, product.picture, product.price];
                    }
                    // { id: 1, col1: 'Hello', col2: 'World', col3: 'picture', col4: 'price' },
                    // { id: 2, col1: 'XGrid', col2: 'is Awesome', col3: 'picture', col4: 'price' },
                    // { id: 3, col1: 'Material-UI', col2: 'is Amazing', col3: 'picture', col4: 'price' },
                    console.log(products.data);
                    for (let i = 0; i < products.data.length; i++) {
                        rows.push({ id: products.data[i]._id, type: products.data[i].type, name: products.data[i].name, picture: products.data[i].picture, price: products.data[i].price });
                    }
                }
            }
            catch (error) {
                console.log(error);
            }
        }, 1000);
        // return rows;
        // }
        // let rows = start();
        const columns = [
            { field: 'type', headerName: 'type', width: 150 },
            { field: 'name', headerName: 'name', width: 150 },
            { field: 'picture', headerName: 'picture', width: 150 },
            { field: 'price', headerName: 'price', width: 150 },
            // { id: 'name', label: 'Name', minWidth: 170 },
            // { id: 'type', label: 'Type', minWidth: 100 },
            // {
            //     id: 'picture',
            //     label: 'Picture',
            //     minWidth: 170,
            //     align: 'right',
            //     format: (value) => value.toLocaleString('en-US'),
            // },
            // {
            //     id: 'price',
            //     label: 'Price',
            //     minWidth: 170,
            //     align: 'right',
            //     format: (value) => value.toLocaleString('en-US'),
            // },
        ];
        // let func = async () => {
        //     await setTimeout(() => {

        //     }, 1000)
        // };
        // func();
        // rows.push(products.data.map(product => { console.log(product); createData(product.name, product.type, product.picture, product.price) }))


        return (
            <div style={{ height: 400, width: '100%' }}>
                <DataGrid id={Math.random()} rows={rows} columns={columns} pageSize={10} checkboxSelection />
                {/* <DataGrid /> */}
            </div >
        );
    }
}

export default withRouter(ProductList);
