import React, { Component } from 'react';
import { Link, withRouter, Redirect } from 'react-router-dom';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import api from '../api'
// import AppNavbar from './AppNavbar';

export class ProductEdit extends Component {

  emptyProduct = {
    type: '',
    name: '',
    picture: '',
    price: 0
  };

  constructor(props) {
    super(props);
    this.state = {
      item: this.emptyProduct
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async componentDidMount() {
    if (this.props.match.params.id !== 'new') {
      const product = await (await fetch(`/api/product/${this.props.match.params.id}`)).json();
      this.setState({item: product});
    }
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let item = {...this.state.item};
    item[name] = value;
    this.setState({item});
    // console.log(item);
  }

  async handleSubmit(event) {
    event.preventDefault();
    const {item} = this.state;

    // await fetch('/api/product', {
    //   method: (item.id) ? 'PUT' : 'POST',
    //   headers: {
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json'
    //   },
    //   body: JSON.stringify(item),
    // });
    api.insertProduct(item);
    // return <Redirect to="/products" />
    // console.log('item ' + item);
    // this.props.history.push('/products');
  }

  render() {
    const {item} = this.state;
    const title = <h2>{item.id ? 'Edit product' : 'Add product'}</h2>;

    return <div>
      <Container>
        {title}
        <Form onSubmit={this.handleSubmit}>
          <FormGroup>
            <Label for="type">type</Label>
            <Input type="text" name="type" id="type" value={item.type || ''}
                   onChange={this.handleChange} autoComplete="type"/>
          </FormGroup>
          <FormGroup>
            <Label for="name">name</Label>
            <Input type="text" name="name" id="name" value={item.name || ''}
                   onChange={this.handleChange} autoComplete="name"/>
          </FormGroup>          
          <FormGroup>
            <Label for="picture">picture</Label>
            <Input type="text" name="picture" id="picture" value={item.picture || ''}
                   onChange={this.handleChange} autoComplete="picture"/>
          </FormGroup>
          <FormGroup>
            <Label for="price">price</Label>
            <Input type="text" name="price" id="price" value={item.price || 0}
                   onChange={this.handleChange} autoComplete="price"/>
          </FormGroup>
          <FormGroup>
            <Button color="primary" type="submit">Save</Button>{' '}
            <Button color="secondary" tag={Link} to="/products">Cancel</Button>
          </FormGroup>
        </Form>
      </Container>
    </div>
  }
}

export default withRouter(ProductEdit);
