import React, { useState, useEffect } from 'react';
import '../App.css';
import api from '../api';
// import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';
// import Snackbar from '@material-ui/core/Snackbar';
// import FormDialog from '../components/dialog.form';

export function Cups() {
    const [products, setProducts] = useState([]);
    // const [state, setState] = useState({
    //     open: false,
    //     vertical: 'bottom',
    //     horizontal: 'right',
    // });
    // const { vertical, horizontal, open } = state;

    useEffect(() => {
        async function load() {
            await api.getAllProducts().then(products => {
                setProducts(products.data.filter(product => (product.type === 'cup')))
            })
        }
        load();
    }, [])

    const handleBuy = (product, i) => {
        localStorage.setItem(`productId${i}`, `${product._id}` + `${product.type}` + `${product.name}` + `${product.picture}`);
        console.log(product)
        // setState({ open: true })
    }

    // const handleClose = () => {
    //     setState({ open: false });
    //   };

    return (
        <div>
            <Grid container className="contentWrapper" spacing={1} style={{ 'width': '70%', margin: 'auto' }}>
                {products.map((product, i) => (
                    <Grid item key={i} className='contentBlock' style={{ margin: '10px 10px' }}>
                        <Paper className="paper">
                            <li><img src={'/imgs/' + product.picture + '.jpg'} alt={product.name + ' picture'} className="picture" /></li>
                            <li className="productName">{product.name}</li>
                            <li className="productPrice">{'₴' + product.price}</li>
                            <li className="productBuy"><Button variant="contained" onClick={() => { handleBuy(product, i) }}>Купить</Button></li>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
            {/* <Snackbar
                anchorOrigin={{ vertical, horizontal }}
                open={open}
                onClose={handleClose}
                message="I love snacks"
                key={vertical + horizontal}
            /> */}
        </div>
    )
}

export default Cups;
