import React, { useState, useEffect } from 'react';
import '../App.css';
import api from '../api';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';

const defaultValues = {
    name: "",
    surname: "",
    email: "",
    city: "",
    shipping: "",
    socType: "",
    socValue: "",
    paid: false,
    sent: false,
    products: "",
    price: "",
};

export function Order() {
    const [formValues, setFormValues] = useState();

    function loadCart() {
        if (localStorage.getItem('podarok-print-en')) {
            let cartList = [];
            let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
            let pr = 0;
            for (let i = 0; i < stored.cart.cartItems.length; i++) {
              cartList.push(stored.cart.cartItems[i]);
            }
            for (let t = 0; t < cartList.length; t++) {
            //   console.log(cartList[t].price, cartList[t].quantity, price, cartList[t]);
            //   console.log(cartList);
              pr += cartList[t].price * cartList[t].quantity;
              setFormValues({products: cartList[t]._id})
            }
            // setPrice(pr);
            // setItems(cartList);
            setFormValues({ price: pr });
          }
        // const stored = JSON.parse(localStorage.getItem('podarok-print-en'));
        // console.log(stored);
        // setFormValues(stored.cartItems)
    }

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormValues({
            ...formValues,
            [name]: value,
        });
    };


    useEffect(() => {
        loadCart();
    }, []);

    return (
        <div>
            {formValues.products.map((item) => (
            <Grid item className='contentBlock' style={{ width: '100%', backgroundColor: 'white' }}>
              <Paper className="paper" style={{ display: "flex" }}>
                <li><img src={'/imgs/' + item.picture + '.jpg'} alt={item.name + ' picture'} className="picture" style={{ width: '100px', height: '100px', margin: 'auto' }} /></li>
                <li className="productName" style={{ margin: 'auto' }}>{item.name}</li>
                <li style={{ margin: 'auto' }}>{/*<Button onClick={() => { HandleRemoveQuantity(item) }}>-</Button>*/}{item.quantity}{/*<Button onClick={() => { HandleAddQuantity(item) }}>+</Button>*/}</li>
                <li className="productPrice" style={{ margin: 'auto' }}>{'₴' + item.price * item.quantity}</li>
                {/* <li style={{ margin: 'auto' }}><Button onClick={() => { HandleRemoveItem(item) }}>X</Button></li> */}
              </Paper>
            </Grid>
          ))}
            <Grid container alignItems="center" justify="center" direction="column">
                <Grid item>
                    <TextField
                        id="name-input"
                        name="name"
                        label="Имя"
                        type="text"
                        value={formValues.name}
                        onChange={handleInputChange}
                        required={true}
                        fullWidth={true}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="surname-input"
                        name="surname"
                        label="Фамилия"
                        type="text"
                        value={formValues.surname}
                        onChange={handleInputChange}
                        required={true}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="city-input"
                        name="city"
                        label="Город"
                        type="text"
                        value={formValues.city}
                        onChange={handleInputChange}
                        required={true}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="shipping-address"
                        name="shipping"
                        label="Отделение НП"
                        type="text"
                        value={formValues.shipping}
                        onChange={handleInputChange}
                        required={true}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="email-input"
                        name="email"
                        label="Email"
                        type="email"
                        value={formValues.email}
                        onChange={handleInputChange}
                        required={true}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="price-input"
                        name="price"
                        type="number"
                        hidden={true}
                        value={formValues.price}
                        // onChange={handleInputChange}
                        required={true}
                        disabled={true}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="products-input"
                        name="products"
                        type="text"
                        hidden={true}
                        value={formValues.products}
                        // onChange={handleInputChange}
                        required={true}
                        disabled={true}
                    />
                </Grid>
            </Grid>
        </div>
    )
}
