import React, { useState, useEffect } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Button, TextField, Container } from '@material-ui/core';
// import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import api from '../api'
// import AppNavbar from './AppNavbar';

export function ProductAdd (){

  let emptyProduct = {
    type: '',
    name: '',
    picture: 'pic',
    price: 0
  };

  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     item: this.emptyProduct
  //   };
  //   this.handleChange = this.handleChange.bind(this);
  //   this.handleSubmit = this.handleSubmit.bind(this);
  // }
  const [product, setProduct] = useState(emptyProduct);

  // async componentDidMount() {
  //   if (this.props.match.params.id !== 'new') {
  //     const product = await (await fetch(`/api/product/${this.props.match.params.id}`)).json();
  //     this.setState({ item: product });
  //   }
  // }

  // useEffect(() => {
  //   async function load() {
  //     await api.getAllProducts().then(products => {
  //         setProducts(products.data.filter(product => (product.type === 'cup')))
  //     })
  // }
  // load();
  // }, [])

  function handleChange(event) {
    // const target = event.target;
    // const value = target.value;
    // const name = target.name;
    // let item = { ...this.state.item };
    // item[name] = value;
    // this.setState({ item });
    // console.log(item);
    const {type, name, picture, price} = event.target;
    setProduct({
      ...product,
      [type]: type,
      [name]: name,
      [picture]: picture,
      [price]: price,
    })
  }

  function handleSubmit() {
    api.insertProduct(product);
  //   event.preventDefault();
  //   const { item } = this.state;
  //   api.insertProduct(item);
  //   this.props.history.push('/products');
  }

  // render() {
    // const { item } = this.state;
    // const title = <h2>{product.id ? 'Edit product' : 'Add product'}</h2>;

    return <div>
      <Container>
        {/* {title} */}
        <form onSubmit={handleSubmit}>
          <TextField label="type" type="text" name="type" id="type" value={product.type || ''}
            onChange={handleChange} />
          {/* <InputLabel id="demo-simple-select-label">Type</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="type"
            name="type"
          // value={type}
          // onChange={changeType}
          >
            <MenuItem value={'cup'}>Кружка</MenuItem>
            <MenuItem value={'t-shirt'}>Футболка</MenuItem>
          </Select> */}
          <TextField label="name" type="text" name="name" id="name" value={product.name || ''}
            onChange={handleChange} />
          <TextField label="picture" type="text" name="picture" id="picture" value={product.picture || ''}
            onChange={handleChange} />
          {/* <input accept="image/*" id="contained-button-file" multiple type="file" hidden />
          <label htmlFor="contained-button-file">
            <Button variant="contained" color="primary" component="span">Upload</Button>
          </label> */}
          <TextField label="price" type="text" name="price" id="price" value={product.price || 0}
            onChange={handleChange} />
          <Button color="primary" type="submit">Save</Button>{' '}
          <Button color="secondary" tag={Link} to="/products">Cancel</Button>
        </form>
      </Container>
    </div>
  }
// }

export default withRouter(ProductAdd);
