import React from 'react';
import { Link } from 'react-router-dom';
import ReactTable from 'react-table'
import { Button, ButtonGroup, Container, Table } from 'reactstrap'
import api from '../api'

import styled from 'styled-components'

import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

export class ProductList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { products: [] };
        // this.remove = this.remove.bind(this);
    }

    componentDidMount = async () => {
        await api.getAllProducts().then(products => {
            this.setState({
                products: products.data.data,
                // isLoading: false,
            })
        })
        // fetch('/api/products')
        //     .then(response => response.json())
        //     .then(data => this.setState({ products: data }));
    }

    deleteUser = event => {
        event.preventDefault();
        if (window.confirm(`Do you want to delete the product ${this.props.id} permanently`)) {
            api.deleteProductById(this.props.id);
            window.location.reload();
        }
    }
    // await fetch(`/api/product/${id}`, {
    //     method: 'DELETE',
    //     headers: {
    //         "Accept": "application/json",
    //         "Content-type": "application/json"
    //     }
    // }).then(() => {
    //     let updatedProducts = [...this.state.products].filter(i => i.id !== id);
    //     this.setState({ products: updatedProducts });
    // });


    render() {
        const { movies, isLoading } = this.state
        console.log('TCL: MoviesList -> render -> movies', movies)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Rating',
                accessor: 'rating',
                filterable: true,
            },
            {
                Header: 'Time',
                accessor: 'time',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
            {
                Header: '',
                accessor: '',
                Cell: function (props) {
                    return (
                        <span>
                            <DeleteMovie id={props.original._id} />
                        </span>
                    )
                },
            },
            {
                Header: '',
                accessor: '',
                Cell: function (props) {
                    return (
                        <span>
                            <UpdateMovie id={props.original._id} />
                        </span>
                    )
                },
            },
        ]

        let showTable = true
        if (!movies.length) {
            showTable = false
        }

        return (
            <Wrapper>
                {showTable && (
                    <ReactTable
                        data={movies}
                        columns={columns}
                        loading={isLoading}
                        defaultPageSize={10}
                        showPageSizeOptions={true}
                        minRows={0}
                    />
                )}
            </Wrapper>
        )
        // 
        const { products } = this.state;
        const productList = products.map(product => {
            return <tr key={product.id}>
                <td style={{ whiteSpace: 'nowrap' }}>{product.type}</td>
                <td>{product.name}</td>
                <td>{product.picture}</td>
                <td>{product.price}</td>
                <td>
                    <ButtonGroup>
                        <Button to={'/products/' + product.id}>Edit</Button>
                        <Button onClick={() => this.remove(product.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });
        return (
            <div>
                <Container fluid>
                    <div>
                        <Button tag={Link} to="/products/new">Add Product</Button>
                    </div>
                    <h3>Product List</h3>
                    <Table>
                        <thead>
                            <tr>
                                <th width="25%">type</th>
                                <th width="25%">name</th>
                                <th width="25%">picture</th>
                                <th width="25%">price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {productList}
                        </tbody>
                    </Table>
                </Container>
            </div>
        );
    }
}

// export default ProductList;
