import React, { useState, useEffect, useCallback } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Slider from "@material-ui/core/Slider";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import Paper from '@material-ui/core/Paper';

const defaultValues = {
  name: "",
  age: 0,
  gender: "",
  os: "",
  favoriteNumber: 0,
};

export default function FormDialog() {
  const [open, setOpen] = useState(false);
  const [items, setItems] = useState([]);
  const [city, setCity] = useState(false);
  const [update, setUpdate] = useState(false);

  const [formValues, setFormValues] = useState(defaultValues);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  useEffect(() => {
    loadCart();
  }, []);

  const handleSliderChange = (name) => (e, value) => {
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formValues);
  };


  const loadCart = () => {
    if (localStorage.getItem('podarok-print-en')) {
      let cartList = [];
      let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
      for (let i = 0; i < stored.cart.cartItems.length; i++) {
        cartList.push(stored.cart.cartItems[i]);
      }
      setItems(cartList);
      // console.log(items);
    }
  }

  const handleClickOpen = () => {
    setOpen(true);
    // loadCart();
  };

  const handleClose = () => {
    setOpen(false);
  };

  const HandleAddQuantity = (item) => {
    if (item.quantity >= 50) {
      item.quantity = 50;
      // setCity(item.quantity);
    }
    else {
      item.quantity++;
      // setCity(item.quantity);
    }
    // loadCart();
    setUpdate(true);
  }

  const HandleRemoveQuantity = (item) => {
    if (item.quantity <= 1) {
      item.quantity = 1;
      // setCity(item.quantity);
    }
    else {
      item.quantity--;
      // setCity(item.quantity);
    }
    // loadCart();
    setUpdate(true);
  }

  const HandleRemoveItem = (item) => {
    // useEffect(() => {
    let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
    for (let i = 0; i < stored.cart.cartItems.length; i++) {
      if (stored.cart.cartItems[i]._id === `${item._id}`) {
        // delete stored.cart.cartItems[i];
        // stored.cart.cartItems[i].display = 'hidden';
        let t = stored.cart.cartItems[0];
        stored.cart.cartItems[0] = stored.cart.cartItems[i];
        stored.cart.cartItems[i] = t;
        stored.cart.cartItems.shift();
        localStorage.setItem('podarok-print-en', JSON.stringify(stored));
      }
    }
    console.log(stored);
    // localStorage.setItem('podarok-print-en', JSON.stringify(stored));
    // setCity();
    loadCart();
    setUpdate(true);
  };

  // useEffect(() => {
  //   loadCart();
  // }, [])

  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Корзина
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title" scroll='body' fullWidth={true} maxWidth={'sm'}>
        <DialogTitle id="form-dialog-title">Ваш заказ</DialogTitle>
        {items.map((item) => (
          <Grid item className='contentBlock' style={{ width: '100%', backgroundColor: 'white' }}>
            <Paper className="paper" style={{ display: `${item.display}` }}>
              <li><img src={'/imgs/' + item.picture + '.jpg'} alt={item.name + ' picture'} className="picture" style={{ width: '100px', height: '100px', margin: 'auto' }} /></li>
              <li className="productName" style={{ margin: 'auto' }}>{item.name}</li>
              <li style={{ margin: 'auto' }}><Button variant="contained" onClick={() => { HandleRemoveQuantity(item) }}>-</Button>{item.quantity}<Button variant="contained" onClick={() => { HandleAddQuantity(item) }}>+</Button></li>
              <li className="productPrice" style={{ margin: 'auto' }}>{'₴' + item.price * item.quantity}</li>
              <li style={{ margin: 'auto' }}><Button onClick={() => { HandleRemoveItem(item) }}>X</Button></li>
            </Paper>
          </Grid>
        ))}
        <DialogContent>
          <FormGroup row>
            <FormControlLabel
              control={
                <Checkbox checked={city} onChange={(event) => setCity(event.target.checked)} />
              }
              label="Доставка в город Энергодар"
            />
            {!city ? <form onSubmit={handleSubmit}>
              <Grid container alignItems="center" justify="center" direction="column">
                <Grid item>
                  <TextField
                    id="name-input"
                    name="name"
                    label="Name"
                    type="text"
                    value={formValues.name}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="age-input"
                    name="age"
                    label="Age"
                    type="number"
                    value={formValues.age}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item>
                  <FormControl>
                    <FormLabel>Gender</FormLabel>
                    <RadioGroup
                      name="gender"
                      value={formValues.gender}
                      onChange={handleInputChange}
                      row
                    >
                      <FormControlLabel
                        key="male"
                        value="male"
                        control={<Radio size="small" />}
                        label="Male"
                      />
                      <FormControlLabel
                        key="female"
                        value="female"
                        control={<Radio size="small" />}
                        label="Female"
                      />
                      <FormControlLabel
                        key="other"
                        value="other"
                        control={<Radio size="small" />}
                        label="Other"
                      />
                    </RadioGroup>
                  </FormControl>
                </Grid>
                <Grid item>
                  <FormControl>
                    <Select
                      name="os"
                      value={formValues.os}
                      onChange={handleInputChange}
                    >
                      <MenuItem key="mac" value="mac">
                        Mac
              </MenuItem>
                      <MenuItem key="windows" value="windows">
                        Windows
              </MenuItem>
                      <MenuItem key="linux " value="linux">
                        Linux
              </MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item>
                  <div style={{ width: "400px" }}>
                    Favorite Number
            <Slider
                      value={formValues.favoriteNumber}
                      onChange={handleSliderChange("favoriteNumber")}
                      defaultValue={1}
                      step={1}
                      min={1}
                      max={3}
                      marks={[
                        {
                          value: 1,
                          label: "1",
                        },
                        {
                          value: 2,
                          label: "2",
                        },
                        {
                          value: 3,
                          label: "3",
                        },
                      ]}
                      valueLabelDisplay="off"
                    />
                  </div>
                </Grid>
                <Button variant="contained" color="primary" type="submit">
                  Submit
        </Button>
              </Grid>
            </form>
              : null}
          </FormGroup>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
