import React, { useState, useEffect } from 'react';
import '../App.css';
import api from '../api';
// import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';
// import Snackbar from '@material-ui/core/Snackbar';
// import FormDialog from '../components/dialog.form';

export function Cups() {
    const [products, setProducts] = useState([]);
    // const [state, setState] = useState({
    //     open: false,
    //     vertical: 'bottom',
    //     horizontal: 'right',
    // });
    // const { vertical, horizontal, open } = state;

    useEffect(() => {
        async function load() {
            await api.getAllProducts().then(products => {
                setProducts(products.data.filter(product => (product.type === 'cup')))
            })
        }
        load();
    }, [])

    function find(array, id) {
        if (typeof array != 'undefined') {
          for (var i = 0; i < array.length; i++) {
            if (array[i].id == id) return [id];
            var a = find(array[i].sub, id);
            if (a != null) {
              a.unshift(array[i].id);
              return a;
            }
          }
        }
        return null;
      }

    const handleBuy = (product, i) => {
        if(localStorage.getItem('podarok-print-en')){
            let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
            console.log(stored);
            stored.cart.cartItems += [{"_id":`${product._id}`, "type":`${product.type}`, "name":`${product.name}`, "picture":`${product.picture}`, "quantity":1}];
            localStorage.setItem('podarok-print-en', JSON.stringify(stored));
            if (find(stored, product._id)){
                console.log("FINDED!!!")
            }
        }
        else{
        let string = {"cart":{"cartItems":[{"_id":`${product._id}`, "type":`${product.type}`, "name":`${product.name}`, "picture":`${product.picture}`, "quantity":1}]}};
        localStorage.setItem('podarok-print-en', JSON.stringify(string));
        console.log(product)
        }
    }

    // const handleClose = () => {
    //     setState({ open: false });
    //   };

    return (
        <div>
            <Grid container className="contentWrapper" spacing={1} style={{ 'width': '70%', margin: 'auto' }}>
                {products.map((product, i) => (
                    <Grid item key={i} className='contentBlock' style={{ margin: '10px 10px' }}>
                        <Paper className="paper">
                            <li><img src={'/imgs/' + product.picture + '.jpg'} alt={product.name + ' picture'} className="picture" /></li>
                            <li className="productName">{product.name}</li>
                            <li className="productPrice">{'â‚´' + product.price}</li>
                            <li className="productBuy"><Button variant="contained" onClick={() => { handleBuy(product, i) }}>ÐšÑƒÐ¿Ð¸Ñ‚ÑŒ</Button></li>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
            {/* <Snackbar
                anchorOrigin={{ vertical, horizontal }}
                open={open}
                onClose={handleClose}
                message="I love snacks"
                key={vertical + horizontal}
            /> */}
        </div>
    )
}

export default Cups;
