import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import api from '../api';

const defaultValues = {
  name: "",
  surname: "",
  email: "",
  city: "",
  shipping: "",
  socType: "",
  socValue: "",
  paid: false,
  sent: false,
  products: "",
  price: "",
};

export default function FormDialog() {
  const [open, setOpen] = useState(false);
  const [items, setItems] = useState([]);
  const [city, setCity] = useState(false);
  const [update, setUpdate] = useState();
  const [price, setPrice] = useState(0);
  const [formValues, setFormValues] = useState(defaultValues);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  useEffect(() => {
    loadCart();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(formValues);
    window.location.href = `/order`;
  };

  const loadCart = () => {
    if (localStorage.getItem('podarok-print-en')) {
      let cartList = [];
      let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
      let pr = 0;
      for (let i = 0; i < stored.cart.cartItems.length; i++) {
        cartList.push(stored.cart.cartItems[i]);
      }
      for (let t = 0; t < cartList.length; t++) {
        console.log(cartList[t].price, cartList[t].quantity, price, cartList[t]);
        console.log(cartList);
        pr += cartList[t].price * cartList[t].quantity;
        setFormValues({products: cartList[t]._id})
      }
      setPrice(pr);
      setItems(cartList);
      setFormValues({ price: pr });
    }
  }

  const handleClickOpen = () => {
    setOpen(true);
    loadCart();
  };

  const handleClose = () => {
    setOpen(false);
  };

  const HandleAddQuantity = (item) => {
    let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
    for (let i = 0; i < stored.cart.cartItems.length; i++) {
      if (stored.cart.cartItems[i]._id === `${item._id}`) {
        if (stored.cart.cartItems[i].quantity >= 50) {
          stored.cart.cartItems[i].quantity = 50;
        }
        else {
          stored.cart.cartItems[i].quantity++;
          setPrice(price + Number(stored.cart.cartItems[i].price));
        }
        stored.cart.price = price;
        localStorage.setItem('podarok-print-en', JSON.stringify(stored));
      }
    }
    loadCart();
    setUpdate(item.quantity);
  }

  const HandleRemoveQuantity = (item) => {
    let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
    for (let i = 0; i < stored.cart.cartItems.length; i++) {
      if (stored.cart.cartItems[i]._id === `${item._id}`) {
        if (stored.cart.cartItems[i].quantity <= 1) {
          stored.cart.cartItems[i].quantity = 1;
        }
        else {
          stored.cart.cartItems[i].quantity--;
          setPrice(price - Number(stored.cart.cartItems[i].price));
        }
        stored.cart.price = price;
        localStorage.setItem('podarok-print-en', JSON.stringify(stored));
      }
    }
    loadCart();
    setUpdate(item.quantity);
  }

  const HandleRemoveItem = (item) => {
    let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
    for (let i = 0; i < stored.cart.cartItems.length; i++) {
      if (stored.cart.cartItems[i]._id === `${item._id}`) {
        let t = stored.cart.cartItems[0];
        stored.cart.cartItems[0] = stored.cart.cartItems[i];
        stored.cart.cartItems[i] = t;
        setPrice(price - Number(stored.cart.cartItems[0].quantity * stored.cart.cartItems[0].price));
        stored.cart.cartItems.shift();
        stored.cart.price = price;
        localStorage.setItem('podarok-print-en', JSON.stringify(stored));
      }
    }
    console.log(stored);
    loadCart();
    setUpdate(item.quantity);
  };

  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Корзина
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title" scroll='body' fullWidth={true} maxWidth={'sm'}>
        <DialogTitle id="form-dialog-title">Ваш заказ</DialogTitle>
        <DialogContent>
          {items.map((item) => (
            <Grid item className='contentBlock' style={{ width: '100%', backgroundColor: 'white' }}>
              <Paper className="paper" style={{ display: "flex" }}>
                <li><img src={'/imgs/' + item.picture + '.jpg'} alt={item.name + ' picture'} className="picture" style={{ width: '100px', height: '100px', margin: 'auto' }} /></li>
                <li className="productName" style={{ margin: 'auto' }}>{item.name}</li>
                <li style={{ margin: 'auto' }}><Button onClick={() => { HandleRemoveQuantity(item) }}>-</Button>{item.quantity}<Button onClick={() => { HandleAddQuantity(item) }}>+</Button></li>
                <li className="productPrice" style={{ margin: 'auto' }}>{'₴' + item.price * item.quantity}</li>
                <li style={{ margin: 'auto' }}><Button onClick={() => { HandleRemoveItem(item) }}>X</Button></li>
              </Paper>
            </Grid>
          ))}
          <Typography style={{ float: 'right' }}><b>Сумма {price} грн.</b></Typography>
        </DialogContent>
        {/* <DialogContent style={{ margin: 'auto auto 10px' }}>
          <FormGroup row>
            <Grid container justify="center" direction="column">
              <FormControlLabel
                control={
                  <Checkbox checked={city} onChange={(event) => setCity(event.target.checked)} />
                }
                label="Доставка в город Энергодар"
              />
              <form onSubmit={handleSubmit} method="POST">
                {!city ?
                  <Grid container alignItems="center" justify="center" direction="column">
                    <Grid item>
                      <TextField
                        id="name-input"
                        name="name"
                        label="Имя"
                        type="text"
                        value={formValues.name}
                        onChange={handleInputChange}
                        required={true}
                        fullWidth={true}
                      />
                    </Grid>
                    <Grid item>
                      <TextField
                        id="surname-input"
                        name="surname"
                        label="Фамилия"
                        type="text"
                        value={formValues.surname}
                        onChange={handleInputChange}
                        required={true}
                      />
                    </Grid>
                    <Grid item>
                      <TextField
                        id="city-input"
                        name="city"
                        label="Город"
                        type="text"
                        value={formValues.city}
                        onChange={handleInputChange}
                        required={true}
                      />
                    </Grid>
                    <Grid item>
                      <TextField
                        id="shipping-address"
                        name="shipping"
                        label="Отделение НП"
                        type="text"
                        value={formValues.shipping}
                        onChange={handleInputChange}
                        required={true}
                      />
                    </Grid>
                    <Grid item>
                      <TextField
                        id="email-input"
                        name="email"
                        label="Email"
                        type="email"
                        value={formValues.email}
                        onChange={handleInputChange}
                        required={true}
                      />
                    </Grid>
                    <Grid item>
                      <TextField
                        id="price-input"
                        name="price"
                        type="number"
                        hidden={true}
                        value={formValues.price}
                        // onChange={handleInputChange}
                        required={true}
                        disabled={true}
                      />
                    </Grid>
                    <Grid item>
                      <TextField
                        id="products-input"
                        name="products"
                        type="text"
                        hidden={true}
                        value={formValues.products}
                        // onChange={handleInputChange}
                        required={true}
                        disabled={true}
                      />
                    </Grid>
                  </Grid> :
                  <DialogContent style={{ margin: 'auto auto 10px' }}>
                    <InputLabel id="demo-dialog-select-label">Как нам связаться с вами?</InputLabel>
                    <Select
                      value={formValues.socType}
                      onChange={handleInputChange}
                      input={<Input />}
                      required={true}
                      name="socType"
                    >
                      <MenuItem value={'telegram'}>Telegram</MenuItem>
                      <MenuItem value={'instagram'}>Instagram</MenuItem>
                      <MenuItem value={'viber'}>Viber</MenuItem>
                    </Select>
                    <Input name="socValue" onChange={handleInputChange} value={formValues.socValue} required={true} />
                  </DialogContent>} */}
                <Button onClick={handleSubmit} variant="contained" style={{ width: '100%', backgroundColor: 'green', margin: '10px 0' }} color="primary">Оформить</Button>
              {/* </form>
            </Grid>
          </FormGroup>
        </DialogContent> */}
      </Dialog>
    </div>
  );
}
