import React, { useState, useEffect } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
// import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
// import List from '@material-ui/core/List';
// import ListItem from '@material-ui/core/ListItem';
// import ListItemAvatar from '@material-ui/core/ListItemAvatar';
// import ListItemIcon from '@material-ui/core/ListItemIcon';
// import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
// import ListItemText from '@material-ui/core/ListItemText';
// import Avatar from '@material-ui/core/Avatar';
// import IconButton from '@material-ui/core/IconButton';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
// import Typography from '@material-ui/core/Typography';
import Slider from "@material-ui/core/Slider";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import Paper from '@material-ui/core/Paper';
// import api from '../api';

const defaultValues = {
  name: "",
  age: 0,
  gender: "",
  os: "",
  favoriteNumber: 0,
};

export default function FormDialog() {
  const [open, setOpen] = useState(false);
  const [items, setItems] = useState([]);
  const [city, setCity] = useState(false);

  const [formValues, setFormValues] = useState(defaultValues);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  useEffect(() => {
    loadCart();
  }, [])

  const handleSliderChange = (name) => (e, value) => {
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formValues);
  };


  const loadCart = () => {
    let cartList = [];
    let stored = JSON.parse(localStorage.getItem('podarok-print-en'));
    // console.log(stored)
    for (let i = 0; i < stored.cart.cartItems.length; i++) {
      // let key = stored.cart.cartItems[i];
      cartList.push(stored.cart.cartItems[i]);
      // alert(`${key}: ${localStorage.getItem(key)}`);
    }
    setItems(cartList);
    console.log(items);
  }

  const handleClickOpen = () => {
    setOpen(true);
    loadCart();
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>

      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Корзина
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        {/* <Grid item xs={12} md={6}>
          <Typography variant="h6">
            Avatar with text and icon
          </Typography>
          <div>
            <List>
              
                <ListItem>
                  <ListItemAvatar>
                    <Avatar>
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary="Single-line item"
                  />
                  <ListItemSecondaryAction>
                    <IconButton edge="end" aria-label="delete">
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
            </List>
          </div>
        </Grid> */}
        {/* <Grid container spacing={1} style={{ margin: 'auto' }}> */}
        {items.map((item, i) => (
          <Grid item key={i} className='contentBlock' style={{ width: '100%', backgroundColor: 'white' }}>
            <Paper className="paper" style={{display: 'flex'}}>
              <li><img src={'/imgs/' + item.picture + '.jpg'} alt={item.name + ' picture'} className="picture" style={{width: '100px', height: '100px'}} /></li>
              <li className="productName">{item.name}</li>
              <li className="productPrice">{'₴' + item.price}</li>
              {/* <li className="productBuy"><Button variant="contained" onClick={() => { handleBuy(product, i) }}>Купить</Button></li> */}
            </Paper>
          </Grid>
        ))}
        {/* </Grid> */}
        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
        <DialogContent>
          <FormGroup row>
            <FormControlLabel
              control={
                <Checkbox checked={city} onChange={(event) => setCity(event.target.checked)} />
              }
              label="Доставка в город Энергодар"
            />
            {!city ? <form onSubmit={handleSubmit}>
              <Grid container alignItems="center" justify="center" direction="column">
                <Grid item>
                  <TextField
                    id="name-input"
                    name="name"
                    label="Name"
                    type="text"
                    value={formValues.name}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="age-input"
                    name="age"
                    label="Age"
                    type="number"
                    value={formValues.age}
                    onChange={handleInputChange}
                  />
                </Grid>
                <Grid item>
                  <FormControl>
                    <FormLabel>Gender</FormLabel>
                    <RadioGroup
                      name="gender"
                      value={formValues.gender}
                      onChange={handleInputChange}
                      row
                    >
                      <FormControlLabel
                        key="male"
                        value="male"
                        control={<Radio size="small" />}
                        label="Male"
                      />
                      <FormControlLabel
                        key="female"
                        value="female"
                        control={<Radio size="small" />}
                        label="Female"
                      />
                      <FormControlLabel
                        key="other"
                        value="other"
                        control={<Radio size="small" />}
                        label="Other"
                      />
                    </RadioGroup>
                  </FormControl>
                </Grid>
                <Grid item>
                  <FormControl>
                    <Select
                      name="os"
                      value={formValues.os}
                      onChange={handleInputChange}
                    >
                      <MenuItem key="mac" value="mac">
                        Mac
              </MenuItem>
                      <MenuItem key="windows" value="windows">
                        Windows
              </MenuItem>
                      <MenuItem key="linux " value="linux">
                        Linux
              </MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item>
                  <div style={{ width: "400px" }}>
                    Favorite Number
            <Slider
                      value={formValues.favoriteNumber}
                      onChange={handleSliderChange("favoriteNumber")}
                      defaultValue={1}
                      step={1}
                      min={1}
                      max={3}
                      marks={[
                        {
                          value: 1,
                          label: "1",
                        },
                        {
                          value: 2,
                          label: "2",
                        },
                        {
                          value: 3,
                          label: "3",
                        },
                      ]}
                      valueLabelDisplay="off"
                    />
                  </div>
                </Grid>
                <Button variant="contained" color="primary" type="submit">
                  Submit
        </Button>
              </Grid>
            </form>
              : null}
          </FormGroup>
          {/* <DialogContentText>
            To subscribe to this website, please enter your email address here. We will send updates
            occasionally.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
          /> */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
