import React from 'react';
// import { Link } from 'react-router-dom';
// import ReactTable from 'react-table'
// import { Button, ButtonGroup, Container, Table } from 'reactstrap'
import api from '../api'

// import styled from 'styled-components'

import styled from 'styled-components'
import { useTable } from 'react-table'

import { withRouter } from 'react-router-dom';

// import 'react-table/react-table.css'

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

const Update = styled.div`
    color: #ef9b0f;
    cursor: pointer;
`

const Delete = styled.div`
    color: #ff0000;
    cursor: pointer;
`

class UpdateProduct extends React.Component {
    updateUser = event => {
        event.preventDefault()

        window.location.href = `/products/update/${this.props.id}`
    }

    render() {
        return <Update onClick={this.updateUser}>Update</Update>
    }
}

class DeleteProduct extends React.Component {
    deleteUser = event => {
        event.preventDefault();
        if (window.confirm(`Do you want to delete the product ${this.props.id} permanently`)) {
            api.deleteProductById(this.props.id);
            window.location.reload();
        }
    }
    render() {
        return <Delete onClick={this.deleteUser}>Delete</Delete>
    }
}

export class ProductList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { products: [] };
        // this.remove = this.remove.bind(this);
    }

    componentDidMount = async () => {
        await api.getAllProducts().then(products => {
            this.setState({
                products: products.data.data,
                // isLoading: false,
            })
        })
    }


    render() {
        const { products } = this.state
        console.log('TCL: ProductList -> render -> products', products)

        const columns = [
            // {
            //     Header: 'ID',
            //     accessor: '_id',
            //     filterable: true,
            // },
            {
                Header: 'Name',
                accessor: 'name',
                filterable: true,
            },
            {
                Header: 'Rating',
                accessor: 'rating',
                filterable: true,
            },
            {
                Header: 'Time',
                accessor: 'time',
                Cell: props => <span>{props.value.join(' / ')}</span>,
            },
            {
                Header: '',
                accessor: '',
                Cell: function (props) {
                    return (
                        <span>
                            <DeleteProduct id={props.original._id} />
                        </span>
                    )
                },
            },
            {
                Header: '',
                accessor: '',
                Cell: function (props) {
                    return (
                        <span>
                            <UpdateProduct id={props.original._id} />
                        </span>
                    )
                },
            },
        ]

        let showTable = true
        // if (!products.length) {
        //     showTable = false
        // }


        function Table({ columns, data }) {
            // Use the state and functions returned from useTable to build your UI
            const {
              getTableProps,
              getTableBodyProps,
              headerGroups,
              rows,
              prepareRow,
            } = useTable({
              columns,
              data,
            })
          
            // Render the UI for your table
            return (
              <table {...getTableProps()}>
                <thead>
                  {headerGroups.map(headerGroup => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                      {headerGroup.headers.map(column => (
                        <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                      ))}
                    </tr>
                  ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                  {rows.map((row, i) => {
                    prepareRow(row)
                    return (
                      <tr {...row.getRowProps()}>
                        {row.cells.map(cell => {
                          return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                        })}
                      </tr>
                    )
                  })}
                </tbody>
              </table>
            )
          }
          
        return (
            <div>
                <Wrapper>
                    {showTable && (
                        <Table
                            data={products}
                            columns={columns}
                            // loading={isLoading}
                            defaultPageSize={10}
                            showPageSizeOptions={true}
                            minRows={0}
                        />
                    )}
                </Wrapper>
            </div>
        )
        // 
        // const { products } = this.state;
        // const productList = products.map(product => {
        //     return <tr key={product.id}>
        //         <td style={{ whiteSpace: 'nowrap' }}>{product.type}</td>
        //         <td>{product.name}</td>
        //         <td>{product.picture}</td>
        //         <td>{product.price}</td>
        //         <td>
        //             <ButtonGroup>
        //                 <Button to={'/products/' + product.id}>Edit</Button>
        //                 <Button onClick={() => this.remove(product.id)}>Delete</Button>
        //             </ButtonGroup>
        //         </td>
        //     </tr>
        // });
        // return (
        //     <div>
        //         <Container fluid>
        //             <div>
        //                 <Button tag={Link} to="/products/new">Add Product</Button>
        //             </div>
        //             <h3>Product List</h3>
        //             <Table>
        //                 <thead>
        //                     <tr>
        //                         <th width="25%">type</th>
        //                         <th width="25%">name</th>
        //                         <th width="25%">picture</th>
        //                         <th width="25%">price</th>
        //                     </tr>
        //                 </thead>
        //                 <tbody>
        //                     {productList}
        //                 </tbody>
        //             </Table>
        //         </Container>
        //     </div>
        // );
    }
}

export default withRouter(ProductList);
