import React, { useState, useEffect } from 'react';
import '../App.css';
import api from '../api';
import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';

export function Cups() {
    // constructor(props) {
    //     super(props);
    //     this.state = { products: [] };
    // }

    const [products, setProducts] = useState([]);


    useEffect(() => {
        async function load() {
            await api.getAllProducts().then(products => {
                setProducts(products.data.filter(product => (product.type === 'cup')))
            })
            // filter();
        }
        load();
        // filter();
    }, [])
    // async componentDidMount() {
    // await api.getAllProducts().then(products => {
    // this.setState({
    // products: products.data,
    // })
    // })
    // this.filter();
    // }

    // filter(){
        // let cups = this.state.products.filter(product => (product.type === 'cup'))
        // this.setState({ products: cups });
    // }
    // render() {
        return (
            <Grid container className="contentWrapper" spacing={3}>
                {products.map((product, i) => (
                    <Grid item justify="center" key={i} xs={3} className='contentBlock'>
                        <Paper className="paper">
                            <li><img src={'/imgs/' + product.picture + '.jpg'} alt={product.name + ' picture'} className="picture" /></li>
                            <li className="productName">{product.name}</li>
                            {/* <li>{product.type}</li> */}
                            <li className="productPrice">{'₴' + product.price}</li>
                            <li className="productBuy"><Button variant="contained" tag={Link} to="/order">Купить</Button></li>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
        )
    // }
}

export default Cups;
